import React from 'react'
import { useState, useEffect } from 'react'

export const WhoWinTitle = ({ userChoice, computerChoice, play }) => {
    useEffect(() => {
        winnerSelector()
    }, [userChoice, play])

    const [winner, setWinner] = useState("")

    const winnerSelector = () => {
        switch (userChoice + computerChoice) {
            case "paperrock":
                setWinner("YOU WIN!")
                break;
            case "paperpaper":
                setWinner("THIS IS A TIE!")
                break;
            case "paperscissor":
                setWinner("THE HOUSE WIN!")
                break;
            case "rockscissor":
                setWinner("YOU WIN!")
                break;
            case "rockrock":
                setWinner("THIS IS A TIE!")
                break;
            case "rockpaper":
                setWinner("THE HOUSE WIN!")
                break;
            case "scissorpaper":
                setWinner("YOU WIN!")
                break;
            case "scissorcissor":
                setWinner("THIS IS A TIE!")
                break;
            case "scissorrock":
                setWinner("THE HOUSE WIN!")
                break;
            default:
                console.log(userChoice + computerChoice)
        }
    }

    return (
        <h1>{winner}</h1>
    )
}
