import React, { useState, useEffect } from 'react';

import styled from 'styled-components'

import { Paper } from '../options/paper/paper'
import { Rock } from '../options/rock/rock'
import { Scissors } from '../options/scissors/scissors'


const ComputerWrapper = styled.div`
`


export const Computer = ({ SetPlay, SetPlayUi }) => {
  useEffect(() => {
    computerSelecting();
  }, [])

  const [computerSelection, setComputerSelection] = useState('')
  
  const computerSelecting = () => {
    const randomNum = Math.floor(Math.random()*31)
    console.log(randomNum)
    if(randomNum < 10){
      setComputerSelection((
        <Paper setPlay={SetPlay} SetPlayUi={SetPlayUi}/>
      ))
    }
    else if(randomNum >= 10 && randomNum < 20){
      setComputerSelection((
        <Rock setPlay={SetPlay} SetPlayUi={SetPlayUi}/>
      ))
    }
    else{
      setComputerSelection((
        <Scissors setPlay={SetPlay} SetPlayUi={SetPlayUi}/>
      ))
    }
  }

    return (
        <ComputerWrapper>
        {computerSelection}
        </ComputerWrapper>
    )
}
