import React from 'react';

import styled from 'styled-components'

import './Rules.css'

const Rules = () => {

    const WrapperRules = styled.div`
    background-color:white;
    border-radius:2rem;
    z-index:10;
    display:grid;
    grid-template-columns:1fr;
    grid-template-rows:60px 1fr;
    row-gap:100px
    height:fit-content;
    padding:2%;
    paddin-top:-20px;
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);

    `



        return(
        <WrapperRules>
          <span className="Rules"style={{gridRowStart:'1', gridColumnStart:'1', justifySelf:'left', alignSelf:'start', margin:'0px', marginTop:'-11px',marginLeft:'5%', height:'fit-content', padding:'0px', fontSize:'1.7rem'}}>RULES</span>
          <div style={{gridRowStart:'1', gridColumnStart:'1', width:'fit-content', margin:'0px', justifySelf:'right', alignSelf:'start', height:'15px'}}><svg xmlns="http://www.w3.org/2000/svg" width="50" height="20"><path fill="#3B4262" fill-rule="evenodd" d="M16.97 0l2.122 2.121-7.425 7.425 7.425 7.425-2.121 2.12-7.425-7.424-7.425 7.425L0 16.97l7.425-7.425L0 2.121 2.121 0l7.425 7.425L16.971 0z" opacity=".25"/></svg></div>
        </WrapperRules>
        )

}

export default Rules;
