import React, { useState, useEffect } from 'react';

import { Paper } from './paper/paper'
import { Rock } from './rock/rock'
import { Scissors } from './scissors/scissors'

import { Computer } from '../computer-play/computer-play'

import styled from 'styled-components'

const OptionsWrapper = styled.div`
  display:grid;
  grid-template-columns:1fr 1fr;
  grid-template-rows:1fr 1fr;
  position:absolute;
  top: 60%;
  left: 50%;
  transform: translate(-50%, -50%);
`

const TriangleWrapper = styled.div`
  opacity:${prop => prop.opacity};
  transition: ease-out 500ms;
  z-index:-1; 
  position:absolute;
  top: 55%;
  left: 50%;
  transform: translate(-50%, -50%) scale(1.4);
`

const PaperWrapper = styled.div`
  cursor:${props=> props.cursor};;
  opacity: ${props => props.opacityPaper};
  transition: ease-out 500ms;
  ${props => props.positionPaper}
  margin:50px;
`

const RockWrapper = styled.div`
  cursor:${props=> props.cursor};;
  opacity:${props => props.opacityRock};
  margin:50px;
  ${props => props.positionRock}
  transition: ease-out 500ms;
`

const ScissorsWrapper = styled.div`
  cursor:${props=> props.cursor};
  opacity:${props => props.opacityScissor};
  ${props => props.positionScissor}
  justify-self:center;
  grid-column-start:1;
  grid-column-end:3;
  margin:50px;
  transition: ease-out 500ms;
`

const ComputerWrapper = styled.div`
`



const Options = () => {

  const [onPlay, setOnPlay] = useState(false)

  useEffect(() => {

    setTimeout(() => {
      SetPlayUi();
    }, 50)

  }, [onPlay])

  const [opacity, setOpacity] = useState('1')

  const [positionPaper, SetpositionPaper] = useState('transform:;')
  const [positionRock, SetpositionRock] = useState('transform:;')
  const [positionScissor, SetpositionScissor] = useState('transform:;')

  const [opacityPaper, setOpacityPaper] = useState('1')
  const [opacityRock, setOpacityRock] = useState('1')
  const [opacityScissor, setOpacityScissor] = useState('1')

  const [cursor, setCursor] = useState('pointer')




  const SetPlay = () => {
    setOnPlay(true)
  }

  const SetPlayUi = (type) => {
    onPlay ? setOpacity('0') : setOpacity('1')
    if (type === 'paper') {
      SetpositionPaper('transform: translate(0%, 50%);')
      setOpacityRock('0')
      setOpacityScissor('0')
      setCursor('auto')
    }
    else if (type === 'rock') {
      SetpositionRock('transform: translate(-153%, 50%);')
      setOpacityPaper('0')
      setOpacityScissor('0')

    }
    else if (type === 'scissor') {
      SetpositionScissor('transform: translate(-76%, -103%);')
      setOpacityPaper('0')
      setOpacityRock('0')
    }
  }

  return (
    <OptionsWrapper>
      <PaperWrapper cursor={cursor} positionPaper={positionPaper} opacityPaper={opacityPaper}><Paper SetPlay={SetPlay} SetPlayUi={SetPlayUi} /></PaperWrapper>
      <RockWrapper cursor={cursor} positionRock={positionRock} opacityRock={opacityRock}><Rock SetPlay={SetPlay} SetPlayUi={SetPlayUi} /></RockWrapper>
      <ScissorsWrapper cursor={cursor} positionScissor={positionScissor} opacityScissor={opacityScissor}><Scissors SetPlay={SetPlay} SetPlayUi={SetPlayUi} /></ScissorsWrapper>
      ComputerWrapper
      <TriangleWrapper opacity={opacity}><svg xmlns="http://www.w3.org/2000/svg" width="305" height="277"><path fill="none" stroke="#000" stroke-width="15" d="M291.5 7.5H4.574c3.119 0 52.416 84.667 147.892 254L291.5 7.5z" opacity=".2" /></svg>        </TriangleWrapper>
    </OptionsWrapper>
  )
}

export default Options
