import React, { useState, useEffect } from 'react';
import styled from 'styled-components';

import './score.css'


const ScoreWrapper = styled.div`
height:120px;
width:160px;
border: solid;
border-color:white;
border-radius: 1rem;
align-self:center;
justify-self:right;
margin-right:50px;
background-color:WhiteSmoke;
display:flex;
flex-direction:column;
justify-content:center;
`

const ScoreTitle = styled.h3`
margin:auto;
align-self:flex-start;
color:#464646;
`
const ScoreNumber = styled.h1`
margin:auto;
font-size:4rem;
margin-top:-10%;
color:#464646;
`


const Score = ({scoreValue}) => {

  const [scoreNumber, setScoreNumber] = useState({score: 0})

  useEffect(()=> {
      console.log("anything")
    scoreSelector();
  }, [])



  const scoreSelector = () => {
    if(scoreValue==="computer"){
        setScoreNumber(scoreNumber.score - 1)
    } else if(scoreValue==="user"){
        setScoreNumber(scoreNumber.score + 1)
    } else {
        setScoreNumber(scoreNumber)
    }
  }

 return (
  <ScoreWrapper>
    <ScoreTitle className="Title">SCORE</ScoreTitle>
    <ScoreNumber>{scoreNumber}</ScoreNumber>
  </ScoreWrapper>
 )
}

export default Score
