import React from 'react'
import { useState, useEffect } from 'react'

export const WhoWinTitle = ({ userChoice, computerChoice }) => {
    useEffect(() => {
        winnerSelector()
    }, [userChoice])

    const [winner, setWinner] = useState("")

    const winnerSelector = () => {
        switch (userChoice && computerChoice) {
            case "paper" && "rock":
                setWinner("YOU WIN!")
                break;
            case "paper" && "paper":
                setWinner("THIS IS A TIE!")
                break;
            case "paper" && "scissor":
                setWinner("THE HOUSE WIN!")
                break;
            case "rock" && "scissor":
                setWinner("THE HOUSE WIN!")
                break;
            case "rock" && "scissor":
                setWinner("THE HOUSE WIN!")
                break;
            case "paper" && "scissor":
                setWinner("THE HOUSE WIN!")
                break;
            default:
                console.log("")
        }
    }

    return (
        <h1>This show the winner</h1>
    )
}
