import React, { useState, useEffect } from 'react';
import styled from 'styled-components';

import './score.css'


const ScoreWrapper = styled.div`
height:120px;
width:160px;
border: solid;
border-color:white;
border-radius: 1rem;
align-self:center;
justify-self:right;
margin-right:50px;
background-color:WhiteSmoke;
display:flex;
flex-direction:column;
justify-content:center;
`

const ScoreTitle = styled.h3`
margin:auto;
align-self:flex-start;
color:#464646;
`
const ScoreNumber = styled.h1`
margin:auto;
font-size:4rem;
margin-top:-10%;
color:#464646;
`


const Score = ({score}) => {

  useEffect(()=> {
    scoreSelector();
  }, [score])

  const [scoreNumber, setScoreNumber] = useState(0)

  const scoreSelector = () => {
    switch (score) {
        case "paperrock":
            setScoreNumber(score - 1)
            break;
        case "paperpaper":
            setScoreNumber(score)
            break;
        case "paperscissor":
            setScoreNumber(score + 1)
            break;
        case "rockscissor":
            setScoreNumber(score - 1)
            break;
        case "rockrock":
            setScoreNumber(score)
            break;
        case "rockpaper":
            setScoreNumber(score + 1)
            break;
        case "scissorpaper":
            setScoreNumber(score - 1)
            break;
        case "scissorscissor":
            setScoreNumber(score)
            break;
        case "scissorrock":
            setScoreNumber(score + 1)
            break;
        default:
           setScoreNumber(score + 50)
    }
  }

 return (
  <ScoreWrapper>
    <ScoreTitle className="Title">SCORE</ScoreTitle>
    <ScoreNumber>{score}</ScoreNumber>
  </ScoreWrapper>
 )
}

export default Score
