import React, { useState, useEffect } from 'react';

import { Paper } from './paper/paper'
import { Rock } from './rock/rock'
import { Scissors } from './scissors/scissors'

import styled from 'styled-components'

const OptionsWrapper = styled.div`
  display:grid;
  grid-template-columns:1fr 1fr;
  grid-template-rows:1fr 1fr;
  position:absolute;
  top: 60%;
  left: 50%;
  transform: translate(-50%, -50%);
`

const TriangleWrapper = styled.div`
  z-index:-1; 
  position:absolute;
  top: 60%;
  left: 50%;
  transform: translate(-50%, -50%);
`

const PaperWrapper = styled.div`
`

const RockWrapper = styled.div`
`

const ScissorsWrapper = styled.div`
  justify-self:center;
`



const Options = () => {
  return (
      <OptionsWrapper>
      <Paper/><Rock/><ScissorsWrapper><Scissors/></ScissorsWrapper>
      <TriangleWrapper><svg xmlns="http://www.w3.org/2000/svg" width="305" height="277"><path fill="none" stroke="#000" stroke-width="15" d="M291.5 7.5H4.574c3.119 0 52.416 84.667 147.892 254L291.5 7.5z" opacity=".2"/></svg>        </TriangleWrapper>
      </OptionsWrapper>
  )
}

export default Options
