import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import './App.css';

import LogoAndScore from '../logoAndScore/logoAndScore'
import Rules from '../Rules/Rules'
import Options from '../../hooks/options/options'

const ExternalWrapper = styled.div`
height:100%;
width:100%;
display:grid;
justify-content:center;
margin-top:3%;


`

const InternalWrapper = styled.div`
opacity:${props => props.opacity};
transition:1s;
display:grid;
grid-template-columns:1fr;
grid-template-rows: 1fr 4fr 150px;
`


function App() {
  useEffect( () => {
    changeDisplay() 
  }, [])

  const [opacity, setOpacity] = useState('0.5');
  const [display, setDisplay] = useState(true);
  const [rules, setRules] = useState(0);

  const changeDisplay = (displayValue) => {
    if(!displayValue){
      setDisplay(displayValue)
      setOpacity('0.5')
    }else if(displayValue){
      setDisplay(displayValue)
      setOpacity('1')
    }
  }

  const rulesSet = (rules ) => {
    if(rules){
      setRules(rules + 1)
    }
  }



  return (
    <div id='backgroundImg'>
    <ExternalWrapper>
      <InternalWrapper opacity={opacity}>
        <LogoAndScore/>
        <Options></Options>
        <div id="rulesButton" onClick={rulesSet}><h2>RULES</h2></div>
      </InternalWrapper>
      <Rules changeDisplay={changeDisplay}/>
    </ExternalWrapper>
    </div>
  );
}

export default App;
